import React, { useState } from 'react';

const Login = ({ onLogin }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = () => {
    // Perform your login logic here, such as making API requests to the backend
    // to authenticate the user using the provided username and password.
    // You can use libraries like axios for making HTTP requests.
    const data = { email: email };
    axios.post('http://localhost:8000/comics/create/', data)
        .then((response) => {
            setPost(response.data);
        })
        .catch(error => {
            console.log(error);
        });

    // Assuming a successful login, call the onLogin callback prop to notify the parent component.
    onLogin();
  };

  return (
    <div>
      <h1>Login Page</h1>
      <form onSubmit={handleLogin}>
        <label>
          Username:
          <input type="text" value={username} onChange={(e) => setEmail(e.target.value)} />
        </label>
        <br />
        <label>
          Password:
          <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
        </label>
        <br />
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default Login;
